import "./App.css";
import {useEffect, useState} from 'react';
import Home from "./Components/Home";
import Blog from "./Components/Blog";
import Latest from "./Components/Latest";
import Contact from "./Components/Contact";
import axios from 'axios';
import Login from "./Components/Login";
import Signin from "./Components/Signin";
import Content from "./Components/Content";
import { Navigate, useNavigate } from "react-router-dom";

import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { NavLink,Link, Routes,Route } from "react-router-dom";

function App() {
  const navigate=useNavigate();
  const [inputdata,setinputdata]= useState("");
  const [inputres,setinputres]= useState([{send:"",res:""}]);
  const genbutton=async()=>{
    let obj={send:inputdata,res:"Loading..."}
    setinputres(inputdataa=>[...inputdataa,obj]);
    let response= await axios.post("https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash-latest:generateContent?key=AIzaSyB3k5Kf4fxTXFFbdo8Rh6jv79I0hEVdy0E",
      {"contents":[{"parts":[{"text":inputdata}]}]}
    )


    setinputres(inputress=>inputress.map(item=>
      item.send === inputdata ? {...item,res:response.data.candidates[0].content.parts[0].text} : item
    ));
    setinputdata
  }

  const [postdata,setpostdata]=useState([{content:"loading..",title:"loading..",date:"hh"},]);
  const [latestId,setLatestId]=useState();
  const [islogedin,setlogedin]=useState(false);
  const [userdata,setuserdata]=useState();
  const [searchres,setsearchres]=useState([]);
  let newserdata;
  const findsubstr=(event)=>{
    newserdata=[];
    setsearchres([]);
    let substr= event.target.value;
    postdata.forEach((element)=>{
      if(element.title.includes(substr)){

        newserdata.push(element)
        setsearchres(newserdata);
      }

    });
    console.log(searchres);
  }
  const test=async()=>{
    try{

      const responsee=await fetch("https://blog-app-api-804s.onrender.com/api/test",{
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({"token":document.cookie.substring(6) }),
      });
      let res=await responsee.json();

      console.log(res);
